void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = (fragCoord.xy - 0.5*iResolution.xy) / iResolution.y; // translate the (0,0) point to the center of the screen and then normalize it relatively to y
    vec3 color = vec3(0, 0, 0);

    for (float i = -1.0; i <= 1.0; i += 0.1)
    {
        if (abs(coord.x - i) < 0.003) color = vec3(0.3, 0.3, 0.3);
        if (abs(coord.y - i) < 0.003) color = vec3(0.3, 0.3, 0.3);
    }


    if (abs(coord.x) < 0.003) color = vec3(1, 0, 0);
    if (abs(coord.y) < 0.003) color = vec3(0, 1, 0);



    fragColor = vec4(color,1.0);
}
