#define PI 3.14159

float f(vec2 ndc, float a) {
    float sa = abs(sin(8.0 * a + iTime));
    float l = 75.0 * (1.5 + sin(iTime)) * length(ndc);
    return smoothstep(1.0 - 0.15 * sa, 1.0, sa * sin(l / (1.5 +cos(4.0 * a))));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 ndc = 2.0 * uv - 1.0;
    ndc.y /= iResolution.x/iResolution.y;
    float a = atan(ndc.y, ndc.x);
    vec3 col = vec3(mix(f(ndc, a + PI/6.0), f(ndc, a-PI/6.0), 2.0 * sin(iTime) - 1.0));
    float l = 4.0 * (1.5 + sin(iTime)) * length(ndc);
    col = 1.0/(1.0 + l * l) * mix(col, f(ndc, a) * vec3(1.0, 0.0, 1.0), sin(iTime));
    fragColor = vec4(col,1.0);
}
