vec3 getNormal(vec2 p) {
    vec2 e = vec2(clamp(-iTime/4.+0.5, 0.001, 2.), 0);
    float d = texture(iChannel0, p).r;

    float r = texture(iChannel0, p+e.xy).x;
    float g = texture(iChannel0, p+e.yx).x;
    float b = (texture(iChannel0, p)+e.x).x;
    return normalize(vec3(r, g, b)-d);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord/iResolution.xy);
    vec3 n = getNormal(uv);
    vec3 col = vec3(n.x/2. + 0.5, n.y/2. + 0.5, n.z/2. + 0.5);
    fragColor = vec4(col, 1.0);
}

#define MAX_ITER 128
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec3 col = vec3(0);

    vec2 z = vec2(0);
    vec2 c = (fragCoord * 2. - iResolution.xy) / iResolution.y;
    float trapDist = 0.;
    vec2 zold = vec2(0);
    for (int i = 0; i < MAX_ITER; i++) {
        zold = z;
        z = vec2(z.x*z.x - z.y*z.y, 2.*z.x*z.y) + vec2(c.x-0.4, c.y) * 1.2;
        if (i == 0 || length(z) < trapDist) {
            trapDist = length(z);
        }
        if (length(z) > 2. || z == zold) { break; }
    }
    col = vec3(trapDist);

    fragColor = vec4(col, 1);
}
