//
// 1.Modify the variable named "FREQUENCY" to generate signals of different frequencies
// 2.Try the c_random() function
//

#define FREQUENCY 60.0
#define C_RANDOM

float sin_base_random(vec2 uv)
{
    float random = 0.5 * sin(FREQUENCY * uv.x * uv.y * iTime * 30.0)+0.5;
    return random;
}

float c_random(vec2 inputVec)
{
    float x = inputVec.x;
    float y = inputVec.y;

    float random = sin(sin(x) * cos(y) * iTime*FREQUENCY*100.0) * 0.5 + 0.5;

    return random;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

#ifdef C_RANDOM
    fragColor = vec4(vec3(c_random(uv)),1.0);   
#else
    fragColor = vec4(vec3(sin_base_random(uv)),1.0);   
#endif

}
