const float PI = 3.1415926535897932384626433832795;
//oscilation fn
float osc(float mV, float MV, float i, float t) {
    return mV + (MV - mV) * 0.5 * (sin(2.0 * PI * t / i) + 1.0);
}
//rotation fn
vec2 r2d(vec2 p, float a) {
    float s = sin(a);
    float c = cos(a);
    mat2 rm = mat2(c, -s, s, c);
    return rm * p;
}
//colors fn (by @cras, 2018) see fork for more ref.
vec4 cC(vec2 uv) {
    vec3 p = vec3(uv, 1.0);
    float a = 0.15 * PI * iTime; 
    vec2 rp = r2d(p.xy, a);
    float mX = (sin(iTime * 0.5) + 1.0) * 0.2;
    float mY = (cos(iTime * 0.5) + 1.0) * 0.2;
    vec3 hc = vec3((rp.xy * 1.5 + 0.5) * iResolution.xy / iResolution.y, mX);
    for (int i = 0; i < 45; i++) {
        float xV = osc(1.2, 1.3, 10.0, iTime);
        float yV = osc(0.955, 0.999, 10.0, iTime);
        float zV = osc(0.7, 0.86, 10.0, iTime);
        hc.xzy = vec3(xV, yV, zV) * (abs((abs(hc) / abs(dot(hc, hc)) - vec3(1.0, 1.0, mY))));
    }
    return vec4(hc, 1.0);
}
//The reason for "blurring" was actually to reduce the "noise" for the behavior of color transitions(I failed, but I wanted to keep it anyway =) )
vec4 bl(vec2 uv) {
    float bs =0.1 /255.0; 
    vec4 col = vec4(0.0);
    for (float x = -1.1; x <= 0.5; x++) {
        for (float y = -1.1; y <= 2.5; y++) {
            col += cC(uv + vec2(x, y) * bs);
        }
    }
    return col / 9.0; 
}

void mainImage(out vec4 fc, in vec2 fcCoord) {
    vec2 uv = 2.0*(1.5 * fcCoord - iResolution.xy) / iResolution.y*1.1;
    fc = bl(uv);
}
