void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 res = iResolution.xy;

    float t = iTime;
    float x = -2.4 + 4.8 * fragCoord.x / res.x;
    float y = -1.5 + 3.0 * fragCoord.y / res.y;
    float d = fract(x*x + y*y+sin(iTime));

    vec3 col = vec3(0.5 + 0.5 * cos(d * 3.14159 + t));
    fragColor = vec4(col, 1.0);
}
