void mainImage( out vec4 O, vec2 u )
{
    vec2  R = iResolution.xy, 
          U = 3.3* ( 2.*u - R ) /R.y, o;

    float t = 4e2 - iTime/4., d,
          n = 5e2,
          s = 3.,
          T = iTime*.45,
         rx = 1.+sin(T)/1e4,
         ry = 1.-cos(T)/2e3,
        col = 0.;

    for ( float i=0.; i<=1.; i+=1./n ) {
        o = vec2( sin(t/rx/i), cos(t/ry/i) ) * i * s;
        d = length(  U -  o ) * 18.;
        col += smoothstep(181./R.y,.0, d - i );
        col *= .9975; 
    }

    O = vec4(col*.8);
}

