#define rot(a) mat2(cos(a + vec4(0, 11, 33, 0)))
#define t iTime * .75
#define box vec3(30) 
#define pessego vec3(220, 190, 180) / 10.
#define rubor   vec3(370, 170, 220) / 30.
#define r iResolution.xy
#define rot3d(p, a, b) p.xy *= rot(a), p.zy *= rot(b)

float jolt (float x) {
    float i = floor(x);
    float y = fract(x);
    y = pow(y, 2.5);
    return i + y;
}

vec3 distort(vec3 p){
    float umaDose = .3;
    p.yx *=  rot(umaDose * dot(vec2(cos(t), sin(t)), p.xz));
    return p;
}

void mainImage(out vec4 o, vec2 u) {    
    u = (u - r.xy / 2.) / r.y; o *= .0;

    vec3 id, cor, obj, q, 
         p = vec3(0, 0, -1.), 
         D = vec3(u, 1.);
         p.xz *= rot(.3 * t);

    float i, ddd, dd, d = 1., a, sh, lights = 1., k = 1.;

    while (i++ < 280. && d > .01) {
        a = .5; 
        d = ddd = -.49;
        while ((a += a) < 8.)
            q = distort(p),
            q.x -= t,
            q = fract(q * a) - .5,
            d += dd = length(q * k) * k / a - k/30.,
            cor = dd < .075
                ? rubor * (mod(t + length(p), 2.) > 1.? 8.:1.)
                : pessego;

        q = p;
        id = floor(q);
        q += vec3(0, -.5, -3);
        q = fract(q) - .5;

        rot3d(q, jolt(t * id.x), jolt(t * id.z));
        q = abs(q);

        dd = dot(q, vec3(.5)) - .15;
        dd < d ? cor = box, d = dd : dd;

        p += D * d * .2; 
    }
    o.rgb += 90./i/i + cor/i;
}
