float sdCircle(vec2 p, float r) {
    return length(p - vec2(0.0, 0.0)) - r;
}

vec3 convertRGBtoVec3(vec3 color) {
    return vec3(color.r / 255., color.g / 255., color.b / 255.);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy * 2.0 - 1.;
    float d = sdCircle(uv * 4. - sin(uv.x * iTime * 7.5), 2.5);
    d /= sdCircle(uv - sin(uv.y * iTime * 0.5), 0.);

    vec3 color = vec3(smoothstep(.75, 0.25, d ));
    color = mix(color, vec3(1.0), smoothstep(0.5, 0., d));

    // Draw the circle
    float circle = sdCircle(uv, 0.75);
    color = mix(color, vec3(0.0), smoothstep(0.0, 0.01, circle));
    fragColor = vec4(color * convertRGBtoVec3(vec3(29., 185., 84.)), 1.0);
}
