const float FADE_OUT_START = 2.0;
const float FADE_OUT_DURATION = 0.5;

void mainImage(out vec4 out_color, in vec2 coord) {
    vec2 uv = coord / iResolution.xy;
    vec3 color = texture(iChannel0, uv).rgb;
    float fade = iTime > FADE_OUT_START
        ? iTime < (FADE_OUT_START + FADE_OUT_DURATION)
            ? 1.0 - (iTime - FADE_OUT_START) / FADE_OUT_DURATION
            : 0.0
        : 1.0;

    out_color = vec4(color * fade, 1);
}
