//Credit Inigo Quilez for voronoise https://www.shadertoy.com/view/ldB3zc
//Credit for specular effect to "Shane" https://www.shadertoy.com/view/4tdGWX 


#define ANIMATE
#define SPECULAR

vec2 hash2( vec2 p )
{
	return textureLod( iChannel1, (p+0.5)/256.0, 0.0 ).xy * (textureLod(iChannel0, vec2(p.x * 0.1, 0.25), 0.0).xx + 0.25);
}

vec3 voronoi( in vec2 x )
{
    vec2 n = floor(x);
    vec2 f = fract(x);

	vec2 mg, mr;

    float md = 8.0;
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec2 g = vec2(float(i),float(j));
		vec2 o = hash2( n + g );
		#ifdef ANIMATE
        o = 0.75 + 0.25*sin( iTime + 6.2831*o );
        #endif	
        vec2 r = g + o - f;
        float d = dot(r,r);

        if( d<md )
        {
            md = d;
            mr = r;
            mg = g;
        }
    }

    md = 8.0;
    for( int j=-2; j<=2; j++ )
    for( int i=-2; i<=2; i++ )
    {
        vec2 g = mg + vec2(float(i),float(j));
		vec2 o = hash2( n + g );
		#ifdef ANIMATE
        o = 0.75 + 0.25*sin( iTime + 6.2831*o );
        #endif	
        vec2 r = g + o - f;

        if( dot(mr-r,mr-r)>0.00001 )
        md = min( md, dot( 0.5*(mr+r), normalize(r-mr) ) );
    }

    return vec3( md, mr );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord/iResolution.xx;

    vec3 c = voronoi( 8.0*p );

    float low = mix(0., 1., textureLod( iChannel0, vec2(.1, 0.), 0.).x);
    float mid = mix(0., 1., textureLod( iChannel0, vec2(.5, 0.), 0.).x);
    float high = mix(0., 1., textureLod( iChannel0, vec2(.9, 0.), 0.).x);

	// isolines
    //vec3 col = vec3(pow(c.x, .1)) * (0.5 * sin(2. * c.x) + 0.5) * (vec3(c.y+hash2(p.xx).x * textureLod(iChannel0, vec2(0.1, .5), 0.0).x, pow(c.z*textureLod(iChannel0, vec2(0.5, .5), 0.0).x + hash2(p.yy).x, 1.), 0.5 - c.yz * pow(textureLod(iChannel0, vec2(0.8, .5), 0.0).x + hash2(c.xx).y, .5) * 2.));
    vec3 col = vec3(pow(c.x, 0.2)) * (vec3( pow(low + c.y * hash2(p.xx).x, 0.5) * 1.25, pow(1. - high * c.x - 0.5, 0.8) * 1.5, pow(mid + c.z * hash2(p.xy).y, 1.) * 2. + 0.1));

    // borders	
    //col = mix( vec3(0.), col, smoothstep( 0.0, 0.05, c.x ) );
    // feature points
	//float dd = length( c.yz );
	//col = mix( vec3(.0,0.6,1.), col, smoothstep( 0.0, 0.12, dd) );
	//col += vec3(1.0,0.6,0.1)*(1.0-smoothstep( 0.0, 0.04, dd));

    col = col-smoothstep(.4, 0., col);

    col *= col*col;

    #ifdef SPECULAR
    col *= vec3(1., .8, .6) 
        *  pow(max(normalize(vec3(length(dFdx(col)), length(dFdy(col)), .5/iResolution.y)).z, 0.), .5)
        + .75; 
    #endif

	fragColor = vec4(col,1.0);
}
