void mainImage(out vec4 O,vec2 U){
    vec2 uv = (2.*U - iResolution.xy)/iResolution.y;

    O = vec4(1);

    vec2 b = vec2(0);
    for(float t = 1.0; t>0.;t-=.01){
        float t0 = t + iTime*.6;
        vec2 a = sin(t0*vec2(1,4) + sin(t0*vec2(5,3))*5.+t*.8765)*.8 + t - 1.;
        float h = smoothstep(0.,60.,iTime);
        float f = abs(length(uv - a)-.2*t)*24.*h+t;
        O *= clamp(f,0.,1.);
        uv += (a-b)*exp(-f*h)*2.;
        b = a;
    }

    O *= uv.y*.3 + .4 + texelFetch(iChannel0,ivec2(0),0).x*.3;
}
