// https://twitter.com/zozuar/status/1670815881352421376?s=20

float PI = 3.14;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    vec4 o;
    vec3 p;
    float g,e,R,t = iTime;

    for(float i; i++<100.; o += .01/exp(e*15.+cos(vec4(3,2,2,0)+e+p.y*4.))){
        p = (.5 - fragCoord.xyy/iResolution.y)*g+i/100.;
        e = atan(p.x, p.z = g-8.) + t*.1;
        p.y += 1.5;
        p = vec3(log2(R=length(p)), acos(p.y/R), e);
        p.x = fract(p.x+p.z/PI)-.02; 

        e = length(p.xy)-.8-(sin(t)*.1)-exp((dot(cos(p.xz*20.)+cos(p.xz*19.),iResolution.xy/iResolution.xy))-4.5);
        g += e*=R*.04;
    }

    // Output to screen
    fragColor = o;
}
