// acid test from https://shadertoy.com/view/mslcRM

void mainImage( out vec4 O,  vec2 u )
{ 
    vec2  R = iResolution.xy,
          U = u / R.y;                        //  normalized coordinates
    O = vec4(0);

    for( float j=0.; j < 6.; j += 1./21. ) 
    {
        float w = .1 /  ( 3. + pow( 18., sin( j+ 1e-9*iTime ) ) ), // 0.*iTime kills the bug
              l = length( U - .1*j ) ;        // doing l = () - w then testing l<0 changes #14
        O += float( l < w ) ;                 // these 3 lines dont yield the same result
     // O +=        l < w ? 1. : 0. ; 
     // if        ( l < w ) O++; 
    }
}
