float fbm(float x){
    float f=0.;
    float w=0.5;
    float a=0.;
    f+=w*sin(x);x*=2.;a+=w;w*=0.5;
    f+=w*sin(x);x*=2.01;a+=w;w*=0.5;
    f+=w*sin(x);x*=2.02;a+=w;w*=0.5;
    f+=w*sin(x);x*=2.03;a+=w;w*=0.5;
    f+=w*sin(x);x*=2.04;a+=w;w*=0.5;
    return f;
    }

float F(vec2 uv){
    return uv.x-fbm(uv.y-iTime)*uv.y*0.2;
}
float sphere(vec2 p){
    p.x*=5.8;
    return length(p)-5.;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 q=fragCoord/iResolution.xy*2.-1.;
    uv.x=uv.x*2.-1.;
    uv.y*=10.;
    vec3 col;
    col=mix(col,vec3(0.05,0.05,0.15),step(sphere(uv),0.));
    float sp=uv.y*uv.y*uv.y*0.001+fbm(-uv.y*2.)*0.1;
    float f=smoothstep(0.001+sp,0.,F(uv))*smoothstep(-0.001-sp,0.,F(uv))*step(sphere(uv),0.);
    col+=vec3(0.8,0.2,0.8)*f;
        col+=vec3(0.8,0.2,0.8)*smoothstep(0.1,0.0,abs(sphere(uv)*F(uv)))*step(sphere(uv),0.);
    fragColor = vec4(col,1.0);
}
