void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec4 col=texture(iChannel0,uv);
    if (col.x<5.) col=col*0.;
    else col = mod((float(iTime*100.)+texture(iChannel0,uv))/500.,1.);
    //if (col>0.) {
    //    fragColor = vec4(1.0);
    //} else {
    //    fragColor = vec4(0.0);
    //}
    // Output to screen
    fragColor = vec4(hsv2rgb(vec3(col.x,1.0,col.x-.05)),1.0);//vec4(col,col,col,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    if (iFrame < 2) {
        fragColor = vec4(0);
    } else if (fragCoord.x<=0.5){
        fragColor = vec4(fragCoord.y+5.);
    } else {
        vec4 pc=texture(iChannel0,vec2(fragCoord.x-1.,fragCoord.y)/iResolution.xy);
        if (length(fragCoord.xy-iMouse.xy)<5.0){
            fragColor=vec4(pc+77.);
            return;
        }
        //fragColor=pc;
        //return;   

        if (floor(pc.x/2.)*2. == pc.x) {
            fragColor=vec4(pc.x/2.0);
        } else {
            fragColor=vec4(pc.x*3.0000+1.0);
        }
    }
}

vec3 hsv2rgb(vec3 c) {
  vec4 K = vec4(1.0, 2.0 / 3.0, 1.0 / 3.0, 3.0);
  vec3 p = abs(fract(c.xxx + K.xyz) * 6.0 - K.www);
  return c.z * mix(K.xxx, clamp(p - K.xxx, 0.0, 1.0), c.y);
}
