void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    float center_size = abs(sin(iTime));

	vec2 fade_coord = 1.0 - smoothstep(vec2(center_size), vec2(1.0), abs(2.0*uv-1.0));
    float fade_factor = fade_coord.x * fade_coord.y;

    vec4 fore = texture(iChannel0, uv);
    vec4 back = texture(iChannel1, uv);

    fragColor = mix(back, fore, fade_factor);

}
