float Ign_F32(uint x, uint y)
{
    const float kA = fract(0.06711056 * 52.9829189);
    const float kB = fract(0.00583715 * 52.9829189);
    float v = fract(float(x) * kA);
    v = fract(v + float(y) * kB);
    return v;
}

float Ign_U8(uint x, uint y)
{
    const uint kA = uint(fract(0.06711056 * 52.9829189) * 255.0 + 0.5);
    const uint kB = uint(fract(0.00583715 * 52.9829189) * 255.0 + 0.5);
    uint v = (kA * x + kB * y) & 255u;
    return float(v) * (1.0 / 255.0);
}

float Ign_U16(uint x, uint y)
{
    const uint kA = uint(fract(0.06711056 * 52.9829189) * 65535.0 + 0.5);
    const uint kB = uint(fract(0.00583715 * 52.9829189) * 65535.0 + 0.5);
    uint v = (kA * x + kB * y) & 65535u;
    return float(v) * (1.0 / 65535.0);
}

float Ign_U24(uint x, uint y)
{
    const uint kA = uint(fract(0.06711056 * 52.9829189) * 16777215.0 + 0.5);
    const uint kB = uint(fract(0.00583715 * 52.9829189) * 16777215.0 + 0.5);
    uint v = (kA * x + kB * y) & 16777215u;
    return float(v) * (1.0 / 16777215.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float zoom = mix(1.0, 64.0, iMouse.y / iResolution.y);
    float mouseX = iMouse.x / iResolution.x;

    vec2 uv_L = fragCoord/iResolution.xy;
    vec2 uv_R = uv_L - vec2(0.5, 0.0);

    float resX = round(iResolution.x / zoom);
    float resY = round(iResolution.y / zoom);

    uint x_L = uint(uv_L.x * resX + 0.5);
    uint y_L = uint(uv_L.y * resY + 0.5);
    uint x_R = uint(uv_R.x * resX + 0.5);
    uint y_R = uint(uv_R.y * resY + 0.5);

    float v_f32 = Ign_F32(x_L, y_L);
    float v_u8 = Ign_U8(x_L, y_L);
    float v_u16 = Ign_U16(x_L, y_L);
    float v_u24 = Ign_U24(x_L, y_L);

    float v_L = abs(v_f32 - v_u8);
    float v_R = abs(v_f32 - v_u24);

    float v = (uv_L.x < 0.5) ? v_L : v_R;

    const vec3 c0 = vec3(0.0, 0.0, 1.0);
    const vec3 c1 = vec3(0.0, 0.5, 0.5);
    const vec3 c2 = vec3(0.0, 1.0, 0.0);
    const vec3 c3 = vec3(0.5, 0.5, 0.0);
    const vec3 c4 = vec3(1.0, 0.0, 0.0);

    vec3 col = vec3(0.0, 0.0, 0.0);
    col = mix(col, c0, smoothstep(0.0, 0.2, v));
    col = mix(col, c1, smoothstep(0.2, 0.4, v));
    col = mix(col, c2, smoothstep(0.4, 0.6, v));
    col = mix(col, c3, smoothstep(0.6, 0.8, v));
    col = mix(col, c4, smoothstep(0.8, 1.0, v));

    col = mix(col, col * v, 0.5);
    col = mix(vec3(v, v, v), col, mouseX);

    col *= smoothstep(0.0, 0.002, abs(uv_L.x - 0.5));

    fragColor = vec4(col.x, col.y, col.z, 1.0);
}
