void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
float r = (5.+fragCoord.x)/iTime*sin(0.5);
vec2 ew = vec2(fragCoord.xx*vec2(15.*sin(5.), sin(54.)));
vec3 ew2 = vec3(ew.x*fragCoord.y + sin(ew.y*fragCoord.x)+4.);
    vec2 uv = vec2 (fragCoord.yx/(iResolution.xy+fragCoord.yy*5.4525425));
    vec3 col = 0.8533 + r+.434*cos(iTime+uv.xyx+ew2);
    vec3 tr = vec3(fragCoord.x*fragCoord.y/4., uv.x*
    (7.52*uv.y), col*vec3(sin(6.43),sin(74.764), sin(2.5342)/4.54
    *(fragCoord*vec2(54., 9.))));
    r*=uv.y*sin(ew.y+ew2.z)+sin(-fragCoord.y);
    fragColor = vec4(col+tr/r/sin((iTime+ew2.x)+(fragCoord.x*fragCoord.y)*ew.y/09.),7.0);
}

