void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float s = 32.0;
    float ss = 2.0; // upscale
    vec2 coord = floor(fragCoord.xy / ss);
    coord.x -= iTime * 20.;

    float a = step(s * 0.5, mod(coord.x - coord.y, s));

    //if(fragCoord.x > 2.0 && fragCoord.x < iResolution.x-2.0 && fragCoord.y > 2.0 && fragCoord.y < iResolution.y-2.0) { a = 0.0; }

    fragColor = vec4(vec3(a), 1.0);
}
