float PHI2 = 1.324717957244746025960908854;
float sin_noise(in float x){
   return fract(sin(x*PHI2));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float a = sin_noise(uv.x + iTime*iResolution.x)*2. - 1.;
    float b = float(sin_noise(uv.y + iTime*iResolution.y) < 0.001);
    uv.x += abs(a)*a*b/iResolution.x;

    vec3 color = texture(iChannel0, uv).rgb;
    color = pow(color, vec3(2.5/2.2));
    fragColor = vec4(color, 1.);
}
