#define PI 3.14159265359

vec3 palette(float d){
	return mix(vec3(0.2,0.7,0.9),vec3(1.,0.,1.),d);
}


mat2 rot(float a){
    return mat2(cos(a),sin(a),-sin(a),cos(a));
}

float map(vec3 p){
    float s = 1.;
    float d;
    for( int i = 0 ; i++ < (int(iTime*0.2+0.99))%30 ; ){
        p = abs(p);
        p = p.y > p.x ? p.yxz : p;
        p = p.z > p.y ? p.xzy : p;
        p.xz *= rot(iTime-3.14/6. + float(i));
        p.yz *= rot(iTime+3.14/6. + float(i));
        p = p.z > p.y ? p.xzy : p;
        p.z = abs(p.z);
        p.x = abs(p.x);
        p -= .4;
        p *= 2.;
        s *= 2.;
        d = length(p)/s;
    }
    return (length(vec2(d))-0.1)*.5;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv=fragCoord/iResolution.xy-vec2(0.5,0.5);
    vec3 rd=normalize(vec3(uv,1));
    fragColor = vec4(0.2,0.1,0.,0.0);
    vec3 p=vec3(0,0,-3);
    float d=1.,i;
    for(;++i<99.&&d>.001 &&d<100.;)
    {
    p+=rd*(d=map(p));
    fragColor+=vec4(palette(length(p)*.1)/(1000.*(d)),1.0/(d*100.));
    }
    //if(d<.001)fragColor+=9./i;
}
