void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.xy;
    float t = iTime * 0.5;
    vec3 col = vec3(2.0 * sin(t * 2.5) + 0.1, 0.1 * cos(t * 0.1) + 0.5, 0.5 * sin(t * 0.1) + 0.5);


    for (int i = 0; i < 20; i++) {
        vec2 p = vec2(sin(float(i) * 2.1 + t) * 1.0, cos(float(i) * 1.0 + t) * 0.3);
        float d = distance(uv, p);
        col += vec3(0.5) / (d * 50.0);
    }

    fragColor = vec4(col, 2.0);
}
