#define PI 3.14159265359
#define TWOPI 6.28318530718

float atan2(in float y, in float x)
{
    return atan(y, x) + (y < 0.0 ? TWOPI : 0.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord - vec2(iResolution.x / 2.0, 0.0);

    vec2 polar = vec2(length(uv), atan2(uv.x, -uv.y) / TWOPI);
    float circleSize = iResolution.y * 0.8;

    if (polar.x < circleSize) {
        fragColor = texture(iChannel0, vec2(1.0 - polar.y, polar.x / circleSize));
    } else {
        fragColor = texture(iChannel1, uv / iResolution.xy + vec2(0.5, iTime));
    }
}
