const float samples = 64.;



float func(vec2 uv){
    float x = mod(uv.x*uv.y*20., 1.);

    return x;

}


const uint k = 1103515245U;
vec3 hash( uvec3 x )
{
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;
    x = ((x>>8U)^x.yzx)*k;

    return vec3(x)*(1.0/float(0xffffffffU));
}
//hash by IQ https://www.shadertoy.com/view/XlXcW4

void mainImage( out vec4 O, vec2 U )
{
    vec2 r = iResolution.xy;
    vec2 uv = (2.*U-r)/r.y;


    float x = 0.;

    if (iFrame%120 < 60){

    for(float i = 0.; i<samples; i++){
        x += func(uv + (hash(uvec3(uv, i+iTime)).xy*2.-1.)/r.y);
    }

    x/=samples;

    }else{

    x = func(uv);

    }

    O = vec4(x);
}
