void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   float angle = iTime * 0.02;
   vec2 normalizedCoord = fragCoord / iResolution.xy * 2.0 - 1.0;
   for (float i = 0.0; i < 126.0; i+=1.0) {
       normalizedCoord = abs(normalizedCoord);
       normalizedCoord -=0.5; 
   //^ these vvvv are the parts that changes it
       normalizedCoord *=1.1;//scaling  
       normalizedCoord *= mat2(
           cos(angle), -sin(angle),
           sin(angle), cos (angle)
       );
   }    
   //matrix w/for loop ^

   fragColor = vec4(length(normalizedCoord),
   //move 
   length(normalizedCoord + vec2(0.1, -0.2)), 
   length(normalizedCoord + vec2(-0.4,-0.1)),1.0);
}
