vec3 palette( in float t) {
    vec3 a = vec3(0.336, 0.006, 0.426);
    vec3 b = vec3(0.536, 0.013, 0.134);
    vec3 c = vec3(1.329, 0.368, 0.925);
    vec3 d = vec3(5.642, 4.834, 0.391);

    return a + b*cos( 6.28318*(c*t+d) );
}

mat2 rotate(float angle) {
    return mat2(
        cos(angle), -sin(angle),
        sin(angle), cos(angle)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = ((2.0*fragCoord-iResolution.xy)/iResolution.y)/2.;
    vec2 uv0 = uv;
    vec3 finalColor1 = vec3(0.0);
    vec3 finalColor2 = vec3(0.0);


     for (float i = 0.0; i < 2.; i++) {
        uv = fract(uv*1.)-0.5;
        uv *= rotate(iTime/1.5);
        float d = length(uv);
        d = abs(d);
        d = pow(0.001/d, 1.11111111111);
        d *= exp(-length(uv0));
        d = smoothstep(0.0, 0.5, d);
        vec3 col = palette(-length(uv0)+i*0.5-iTime);

        finalColor1 += col*d;

    }

    fragColor = vec4(finalColor1,1.0);

    for (float i = 0.0; i < 11.; i++) {
        uv = fract(uv*2.)-0.5;
        uv *= rotate(-iTime*3.14);
        float d = length(uv);
        d = sin(d*15.5+iTime)/12.5;
        d = abs(d);
        d = pow(0.005/d, 1.55);
        d *= exp(-length(uv0));
        d = smoothstep(0.0, 0.5, d);
        vec3 col = palette(-length(uv0)+i*0.7+iTime);

        finalColor2 += col*d;

    }

     fragColor = vec4(finalColor2,1.0);
}
