float dither(float id, float v) {
    return mod(round(id), 2.);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 coord = round(fragCoord / 6.);
    vec4 col = vec4(dither(coord.x+coord.y+(iTime * ((coord.x/iResolution.x) + 1.)), fragCoord.y / iResolution.y));
    //fragColor = mod(iTime, .25) > .125 ?  vec4(1.) - col : col;
    fragColor = col;
}
