void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (gl_FragCoord.xy - 0.5 * iResolution.xy) / iResolution.y;

    vec3 col = vec3(0.0);
    float r = 0.17;

    for (float i=0.0,f,a; i<100.0; i++) {

        i+= f = sin(iTime)*0.5 + 0.8;
        a = i / 3.0;

        float dx =  f * r * cos(a) - r * cos(f * a);
        float dy =  f * r * sin(a) - r * sin(f * a);
        col += 0.004 * f / length(uv - vec2(dx , dy));
        col += 0.002 * f  / length(uv);
        //uv*=mat2(cos(iTime*.1+vec4(0,11,33,0))); //Have a look:)
    }

    col *= sin(vec3(0.2, 0.4, 0.5) * iTime) * 0.15 + 0.25;

    fragColor = vec4(col, 1.0);
}
