// Rotation matrix around z axis
// a : Angle
mat3 Rz(float a)
{
  float sa=sin(a);float ca=cos(a);
  return mat3(ca,sa,0.,-sa,ca,0.,0.,0.,1.);
}
// Rotation matrix around y axis
// a : Angle
mat3 Ry(float a)
{
  float s=sin(a);float c=cos(a);
  return mat3(c,0.,-s,
              0.,1.,0.,
              s,0.,c);
}
// Rotation matrix around x axis
// a : Angle
mat3 Rx(float a)
{
  float s=sin(a);float c=cos(a);
  return mat3(1.,0.,0.,
              0.,c,-s,
              0.,s,c);
}

// Rotation matrix around u vec
// a : Angle
mat3 rotate(vec3 a)
{
  return Rx(a.x)+Ry(a.y)+ Rz(a.z);
}




// Compute the ray
//      m : Mouse position
//      p : Pixel
// ro, rd : Ray origin and direction
void Ray(vec2 m,vec2 p,out vec3 ro,out vec3 rd)
{
  float a=3.*3.14*m.x;
  float le=3.5;

  // Origin
  ro=vec3(37.,0.,15.);
  ro*=Rz(a);

  // Target point
  vec3 ta=vec3(0.,0.,1.);

  // Orthonormal frame
  vec3 w=normalize(ta-ro);
  vec3 u=normalize(cross(w,vec3(0.,0.,1.)));
  vec3 v=normalize(cross(u,w));
  rd=normalize(p.x*u+p.y*v+le*w);
}

// Operators

// Union
// a,b : field function of left and right sub-trees
float Union(float a,float b)
{
  return min(a,b);
}
//Inter
float Inter(float a,float b)
{
  return max(a,b);
}
//A sans B
float without(float a,float b)
{
  return max(a,-b);
}

// Primitives

//segment



//plan
float plan(vec3 p, vec3 u, vec3 c)
{
    return dot(p-c,u);
}
// Sphere
// p : point
// c : center of skeleton
// r : radius
float Sphere(vec3 p,vec3 c,float r)
{
  return length(p-c)-r;
}

//translation 
vec3 Translation ( vec3 t, vec3 p ) 
{
    return (p - t);
}

//demisphere
float demisphere(vec3 p,vec3 c,vec3 n,float r)
{
  float v1=Sphere(p,c,r);
  float v2=plan(p,n,c);
  return max(v1,v2);
}
//cylindre

float cylindreinf (vec3 p, vec3 c, float r, vec3 u)
{
    u=normalize(u);
    return(length((p-c)-dot(p-c,u)*u)-r);
}

float cylindre1 (vec3 p, vec3 c, float r, vec3 u, float h)
{
    float v1=cylindreinf(p,c,r,u);
    float v2=plan(p,-u,c-(h*0.5));
    float v3=plan(p,u,c+(h*0.5));
    return max(max(v1,v2),v3);
}

//Capsule
float capsule (vec3 p, vec3 c, float r, vec3 u, float h)
{
    return Union(cylindre1(p,c,r,u,h),Union(
    demisphere(p,vec3(c.x-h*0.5,c.y,c.z),normalize(u),r),
    demisphere(p,vec3(c.x+h*0.5,c.y,c.z),-normalize(u),r)));
}

//PAIN 
float pain(vec3 p, vec3 c, float r, vec3 u, float h)
{
    float v1=capsule (p, c, r, u, h);
    float v2=plan(p,vec3(u.x-1.,u.y+1.,u.z+1.),c);
    float v3=plan(p,vec3(u.x-1.,u.y-1.,u.z-1.),c+2.);
    return Union(max(v1,v2),max(v1,v3));

}

float boite (vec3 p,vec3 b)
{
    vec3 d=abs(p)-b;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}
// bouger boite
float boitev2(vec3 p, vec3 b, vec3 mouv)
{
    vec3 d=Translation(p, mouv);
    return boite(d,b);

}




