#define N(T,l)  U = ( u+u - R ) / R.y * mat2(cos(iDate.w/T+vec4(0,33,11,0))),      \
                O += max(0., l - R.y/2.                              /* AA draw */ \
                      * min( max( abs(U.x), abs(U.y-.3)-.3 ),        /* handle  */ \
                             abs( max(abs(U).x,abs(U).y) - .6 ) ));  /* frame   */

void mainImage( out vec4 O, vec2 u )
{
	vec2 R = iResolution.xy, U;
    O*=0.;
    //N(1e30, 3.)
	N( 6875., 3. )
    N( 573.,  1.5)
    N( 9.55,  1. )

    O = sqrt(O);     // to sRGB
}
