vec3 pal(float i)
{
    vec3 a = vec3(0.953, 0.508, 0.344);
    vec3 b = vec3(0.669, 0.549, 0.979);
    vec3 c = vec3(0.106, 0.338, 1.057);
    vec3 d = vec3(5.044, 5.470, 3.429);

    return a + b * cos(c * i + d);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 final = vec3(0.0);

    vec3 col = pal(length(uv0) - iTime * 0.73);

    for (float i = 0.0; i < 3.0; i++)
    {
        uv = fract(uv * 1.2 * -(cos(0.23 * iTime) + sin(0.17 * iTime))) - 0.5;

        float d = length(uv) * exp(-length(uv0)) / 0.77;

        d = cos(d * 8.0 + sin(1.23 * iTime) * 1.7);
        d = abs(d);

        d = 0.05 / d;
        final += col * d;
    }

    // Output to screen
    fragColor = vec4(final, 1.0);
}
