void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    float t = iTime * 0.5;

    vec3 col = 0.5 + 0.5 * cos(iTime + uv.xyx + vec3(0, 2, 4));

    float circleRadius = 0.1;
    float strokeWidth = 0.005;

    float phase = mod(t, 1.0);
    float r = circleRadius + phase;
    float d = length(uv - vec2(0.5)) - r;
    float circles = smoothstep(-strokeWidth, strokeWidth, d);

    col += vec3(0.0, 0.0, 1.0) * circles;
    col = 1.0 - col;

    fragColor = vec4(col, 1.0);
}
