#define PI 3.1415926538

float sdCircle(vec2 p, float r) {
    return length(p) - r;
}

float HoleCircle(vec2 p, float r1, float r2) {
    return abs(sdCircle(p, r1)) - r2;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2.0f * fragCoord - iResolution.xy) / iResolution.y;

    float sdf = 10000.0f;
    int count = 24;
    for(int i = 0; i < count; i++) {
        float angle = float(i) / float(count) * 2.0f * PI + iTime;
        vec2 pos = vec2(cos(angle), sin(angle)) * (sin(iTime) * 0.5f + 0.5f) * 0.9f;
        float c = HoleCircle(uv - pos, 0.337, 0.009);

        sdf = min(sdf, c);
    }

    float glow = (-sdf + 0.552) * 1.638;
    glow = pow(glow, 3.821);

    vec4 color = (1.0f - smoothstep(0.0f, 0.003f, sdf) + glow) * vec4(0.1176f, 0.4039f, 1.0f, 1.0f);

    fragColor = color;
}
