void mainImage( out vec4 fragColor, in vec2 fragCoord )
{


    vec2 o_trn_p = (fragCoord.xy -iResolution.xy*.5) / iResolution.y;

    vec3 o_max = max(
        vec3(0.2, 1., 0.), 
        vec3(0.0, 0., 1.)
    );
    // o_max will be vec3(0.2, 1., 1.);
    fragColor = vec4(o_max, 1.);
}
