#define C(a,b) cos( length( p - h* vec2(cos( t / a ), sin( t / b ) ) )
void mainImage( out vec4 O, vec2 u )
{
    float t = cos( iTime / 4e2 ) * 1e3;
	vec2 r = iResolution.xy,
         h = 1e2 * r / r.x,
         p = floor( 40.* u / r.x ) - h,
	     v = vec2( cos( t  * .02 ), cos( t *.03 ) ) * h - p;
      t = C( 9.  , 2e2)    * .03 )
        + C( 1e2 , -14.)   * .05 )
        + sin( length( v ) * .04 ) 
        * sin( v.x * .05) 
        * sin( v.y * .07 );
        p = step(1.,mod(u , r.y / h.y /.4) );
      O = (.5 + .5 * cos( 6.3 * ( .3 * t + vec4( .7, .5, .3 ,0) ) ) ) 
      * min( p.x , p.y )
      ;
}
