void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;

    float pi = 3.1415926536;


    float Spirals = 5.0;
    float SpiralR = 2.;
    float SpiralD = 0.2;
    float turnSpeed = 1.;


    SpiralR = SpiralR * ( 1.  +  0.5 * sin(0.5*iTime));
    SpiralD = SpiralD/Spirals/Spirals/SpiralR;
    float Spiraloffset = 2.*pi/Spirals;

    vec3 col = vec3(0,0,0);
    vec3 c = vec3(0,0,0);
    float angle = 0.;
    float dist = 0.;
    float brightness = 0.;

    for(float i = 0. ; i<Spirals ; i++ )
    {
        angle = atan(uv.y/uv.x)+ i*Spiraloffset;   if ( uv.x < 0. ) angle+= pi;
        dist = 0.5*( 1. + sin (    2.*pi*length(uv)/SpiralR   +   angle    - iTime*turnSpeed));

        brightness = SpiralD/dist;
        c = vec3(
        0.5*( 1. + sin (    2.*pi*length(uv)/SpiralR - 2.0*iTime*turnSpeed)),
        0.5*( 1. + sin (    2.*pi*length(uv)/SpiralR - 5.0*iTime*turnSpeed+2.)),
        0.5*( 1. + sin (    2.*pi*length(uv)/SpiralR - 7.0*iTime*turnSpeed+4.)));

        col += brightness*c;
    }

    fragColor = vec4(col,1.0);
}
