void mainImage(out vec4 fragColor,  in vec2 fragCoord)

{
        vec2 u=2.*fragCoord.xy/iResolution.y-1.;
        vec2 m=iMouse.xy/iResolution.xy;
        u*=exp(5.5);
        u+=vec2(-1000,-1000);
        u+=2000.*m;
        int a=(int(u.x) ^ int(u.y));
        int b=int(pow(u.x*u.x+u.y*u.y,0.5))/15;

        fragColor[(a | b)%19]=1.;//int(10.*iTime)
        //fragColor[b%19]=1.;
        //fragColor[a%19]=1.;
}
