// Code by Flopine

// Thanks to wsmind, leon, XT95, lsdlive, lamogui, 
// Coyhot, Alkama,YX, NuSan, slerpy, wwrighter 
// BigWings, FabriceNeyret and Blackle for teaching me

// Thanks LJ for giving me the spark :3

// Thanks to the Cookie Collective, which build a cozy and safe environment for me 
// and other to sprout :)  
// https://twitter.com/CookieDemoparty


// Heavily inspired by a post from the random tiling bot 
// (follow it it's cool :D)
// https://twitter.com/RandomTiling/status/1582712902426931200


#define PI acos(-1.)
#define rot(a) mat2(cos(a),sin(a),-sin(a),cos(a))

#define circle(u,s) (length(u)-s)
#define AAstep(thre, val) smoothstep(-.7,.7,(val-thre)/min(0.03,fwidth(val-thre)))

#define noise(u) textureLod(iChannel0, u, 0.).x


float truchet (vec2 uv)
{
    vec2 id = floor(uv);
    if (noise(id*0.36+iTime*0.001)<.5) uv  *= rot(PI/2.);
    uv = fract(uv)-.5;
    vec2 uu = uv;

    float a = atan(uv.x, uv.y);
    a = cos(a*2.)*.5+.5;
    float w = mix(0.1,0.05, a);
    uv.y = -abs(uv.y)+.44;
    float d = AAstep( w, abs(circle(uv*vec2(.61, 1.),.25)) );

    uv = uu;
    uv.x= abs(abs(uv.x)-.25)-.12;
    d *= AAstep(0.001,circle(uv, 0.05) );

    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    uv *= 4.;
    uv.y -= iTime;
    vec3 col = mix(vec3(0.5,0.5,0.), vec3(.99,0.9,0.4), truchet(uv));

    // Output to screen
    fragColor = vec4(sqrt(col),1.0);
}
