const float PI = 3.14159265359;
const float THICKNESS = 8.0; // Thickness of wave display
const float NUM_WAVE_LENGTHS = 1.0; // Num wavelengths on the screen at the same time

float humpWave(float angle) {
  return abs(sin(angle));
}

float wonkyTriWave(float angle) {
  return (humpWave(angle) - humpWave(angle + PI / 2.)) / 2. + 0.5;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    float xScale = iResolution.x / PI / NUM_WAVE_LENGTHS;
    float yScale = iResolution.y - 2. * THICKNESS;

    fragColor = vec4(1.0, 1.0, 0.0, 1.0);
    float graph = yScale * wonkyTriWave(iTime + fragCoord.x / xScale) + THICKNESS;

    if (fragCoord.y >= graph - THICKNESS && fragCoord.y <= graph + THICKNESS) {
      fragColor = vec4(0.0, 0.0, 0.0, 1.0);
    }
}
