float plot(vec2 uv)
{
    return smoothstep(0.02, 0.0, abs(uv.y - uv.x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    float y = uv.x;
    vec3 col = vec3(y);

    float pct = plot(uv);

    col = (1.0 - pct) * col + pct * vec3(0.0, 1.0, 0.0);

    fragColor = vec4(col,1.0);
}
