void mainImage(out vec4 fragColor, in vec2 fragCoord) {
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

vec4 drawColor = vec4(0.5,0.0,0.7, 1.0);

vec4 RandomColor(in float t)
{
    float r = 0.5*(1.0 + sin(1.23 * t));
    float g = 0.5*(1.0 + sin(2.12 * t));
    float b = 0.5*(1.0 + sin(1.50 * t));
    return vec4(r,g,b,1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord / iResolution.xy;
    vec2 mouse = iMouse.xy / iResolution.xy;

    float dist = distance(mouse, uv);

    float d_thresh = 1.0 - step(0.02, dist);

    if(iMouse.z > 0.0)
    {
        drawColor = RandomColor(iTime);
    }
    else
    {
        drawColor = vec4(0.0,0.0,0.0,1.0);
    }


    vec4 color = texture(iChannel0, uv);
    vec4 paintColor = max(vec4(0.0), d_thresh*drawColor - color);
    color += paintColor;

    fragColor = color;
}
