vec2 N(float angle){
    return vec2(sin(angle),cos(angle));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    vec2 mouse = iMouse.xy / iResolution.xy;
    uv *= 1./(sin(iTime*.4)*(cos(iTime)*10.+10.)+.5);
    uv.x += .5;
    uv.y -= .5;
    vec3 col = vec3(0);

    float scale = 1.;
    vec2 n = N((1./4.) * 3.1415);
    float d = dot(uv, n);
    uv -= n * min(0., d) * 2.;

    n = N((3./4.) * 3.1415);
    d = dot(uv - vec2(1., .0), n);
    uv -= n * max(0., d) * 2.;


    n = N((3./4.) * 3.1415);
/*
    uv -= n * min(0., dot(uv, n)) * 2.;

    col += smoothstep(0.01, 0., abs(d));
*/
    for(int i = 0; i < 4; i++){    
        uv *= 3.;
        uv.x -= 1.5;
        uv.y -= (5./5.);
        scale *= 3.;

        uv.x = abs(uv.x);
        uv.x -= .5;
        uv -= n * min(0., dot(uv, n)) * 2.;
        uv.y += 1.;
        uv -= n * min(0., dot(uv, n)) * 2.;

    }

    d = length(uv - vec2(clamp(uv.x, -1., 1.), 0));
    //col += smoothstep(1./iResolution.y, .0, d/scale);
    //col.rg = uv;
    col += texture(iChannel0, uv).rgb;

    fragColor = vec4(col,1.0);
}
