#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv -= 0.5;
    uv *= 2.0;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = length(uv) > 1.0 ? vec3(0.0) : vec3(1.0);

    float angle = acos(dot(normalize(uv), vec2(1.0, 0.0)));

    float r = angle / (2.0 * PI);
    r *= 8.0;

    if (uv.y <= 0.0)
    {
        r = 8.0 - r;
    }

    r -= iTime * PI * 3.0;

    if (mod(floor(r), 2.0) == 1.0)
    {
        col = vec3(0.0);
    }

    r -= 0.5;

    if (length(uv) > 0.4 && length(uv) < 0.5)
    {
        if (mod(floor(r), 2.0) == 1.0)
        col = vec3(0.0);
    }

    r -= 1.0;

    if (length(uv) > 0.7 && length(uv) < 0.8)
    {
        if (mod(floor(r), 2.0) == 1.0)
        col = vec3(0.0);
    }



    // Output to screen
    fragColor = vec4(col, 1.0);
}
