renderpass.code:
float sdCube(vec2 uv,float r)
{
    uv=abs(uv)-r;
    float i=min(max(uv.x,uv.y),0.),
          o=length(max(uv,0.));
    return i+o;
}
float SDF(vec2 uv)
{
    float s=length(uv-vec2(0))-.2;
    float c=sdCube(uv,.2);
    return mix(s,c,sin(iTime)*.5+.5);
}
void mainImage( out vec4 O, in vec2 C )
{
    vec2 uv = (C-.5*iResolution.xy)/iResolution.y;
    uv*=2.;
    float cseg=SDF(uv)*30.,
          seg=floor(cseg);
    cseg-=seg;

    O.xyz=mix(vec3(.7,.5,.3),vec3(.3,.4,.4),seg/6.);
    O=mix(vec4(0),O,smoothstep(0.,.1,.5-abs(cseg-.5)));
}
