// 灰度转换函数
float greyScale(in vec3 col) {
    return dot(col, vec3(0.2126, 0.7152, 0.0722));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy; // 归一化坐标0～1， 作为取纹理用
    vec3 col = texture(iChannel0, uv).rgb;  // 取图片纹理
    float grey = greyScale(col);  // 灰度转换
    col = vec3( grey );  // 一维变三维
	fragColor = vec4( col, 1.0 ); // 屏幕颜色输出
}
