uvec3 k = uvec3(0x456789abu, 0x6789ab45u, 0x89ab4567u);
uvec3 u = uvec3(1, 2, 3);
const uint MAX_UINT = 0xffffffffu;
uvec2 h22(uvec2 n){
    n ^= (n.yx << u.xy);
    n ^= (n.yx >> u.xy);
    n *= k.xy;
    n ^= (n.yx << u.xy);
    return n * k.xy;
}
float h21(vec2 p){
    uvec2 n = floatBitsToUint(p);
    return float(h22(n).x) / float(MAX_UINT);
}

float vn(vec2 p){
    vec2 n = floor(p);
    float[4] v;
    for (int j = 0; j < 2; j++){
        for (int i = 0; i < 2; i++){
            v[i + 2 * j] = h21(n + vec2(i, j));
        }
    }
    vec2 f = fract(p);
    f = f * f * f * (10.0 - 15.0 * f + 6.0 * f * f);
    return mix(mix(v[0], v[1], f[0]), mix(v[2], v[3], f[0]), f[1]);
}

vec2 g(vec2 p){ 
    float e = 0.01; 
    return 0.8 * (vec2(
        vn(p + vec2(e, 0.0)) - vn(p - vec2(e, 0.0)),
        vn(p + vec2(0.0, e)) - vn(p - vec2(0.0, e))
    )) / e;
}

void mainImage(out vec4 fc, in vec2 fcCoord){
    vec2 p = gl_FragCoord.xy / iResolution.xy;
    p = g(p) * 4.0 + iTime;
    fc.rgb = vec3(g(p), h21(p) * 0.0);
    fc.a = 1.0;
}
