void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col = vec3(1.0);

    float delay = 5.0;

    col.x = abs(sin(iTime / delay));
    col.y = abs(cos(iTime / delay));
    col.z = abs((sin(iTime / delay) + cos(iTime/ delay))/ 2.0);

    if(uv.y >= 0.1 * sin(uv.x + iTime) + 0.5 ||
       uv.y <= 0.1 * sin(uv.x + iTime) - 0.5)
       {
       col = vec3(1.0) - col;
       }

    // Output to screen
    fragColor = vec4(col,1.0);
}
