const vec3 DARK = vec3(0.);
const vec3 GRAY = vec3(100., 100., 100.) / 256.;
const vec3 DARK_GRAY = vec3(47., 47., 47.) / 256.;
const vec3 COL_SKIN = vec3(217., 187., 147.) / 256.;
const vec3 COL_EYE = vec3(242., 231., 182.) / 256.;

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= .6;
    // mirror
    uv.x = abs(uv.x);
    // background color
    vec3 col = GRAY;
    // head
    if (uv.x <= .1 && uv.y >= 0. && uv.y <= .2) {
        col = COL_SKIN;
    }
    // mask
    if (
        (uv.x <= .1 && uv.y >= .12 && uv.y <= .2) || 
        (uv.x <= .2 / 3. && uv. y >= .085 && uv.y <= .12) ||
        (uv.x >= .1 * .1 / .3 && uv.x <= .2 * .1 / .3 && uv.y >= .2 && uv.y <= .24)
    ) {
        col = DARK;
    }
    // eyes
    if (uv.x >= .025 && uv.x <= .055 && uv.y >= .12 && uv.y <= .15) {
        col = COL_EYE;
    }
    // body
    if (uv.x <= .13 && uv.y <= 0. && uv.y >= -.2) {
        col = DARK_GRAY;
    }
    if (uv.x >= .10 && uv.x <= .13 && uv.y <= -.17 && uv.y >= -.2) {
        col = GRAY;
    }
    // cloak
    if (
        (uv.x >= .13 && uv.x <= .16 && uv.y <=  .00 && uv.y >= -.14) ||
        (uv.x >= .10 && uv.x <= .13 && uv.y <= -.10 && uv.y >= -.17) ||
        (uv.x >= .07 && uv.x <= .10 && uv.y <= -.14 && uv.y >= -.20) ||
        (uv.x <= .03 && uv.y <= -.2 && uv.y >= -.23)
    ) {
        col = DARK;
    }
    // bat
    if (
        (uv.x <= .03 && uv.y <= -.03 && uv.y >= -.15) ||
        (uv.x <= .09 && uv.y <= -.06 && uv.y >= -.09) ||
        (uv.x <= .06 && uv.y <= -.09 && uv.y >= -.12)
    ) {
        col = DARK;
    }

    fragColor.rgb = col;
}
