uvec3 k = uvec3(0x456789abu, 0x6789ab45u, 0x89ab4567u);
uvec3 u = uvec3(1, 2, 3);
const uint MAX_UINT = 0xffffffffu;
uvec3 uh(uvec3 n){
    n ^= (n.yzx << u);
    n ^= (n.yzx >> u);
    n *= k;
    n ^= (n.yzx << u);
    return n * k;
}
vec3 hs(vec3 p){
    uvec3 n = floatBitsToUint(p);
    return vec3(uh(n)) / vec3(MAX_UINT);
}
vec4 s(vec4 l, float v){
    bvec4 r = bvec4(step(v, l));
    return r.x ? vec4(v, l.xyz) : r.y ? vec4(l.x, v, l.yz) : r.z ? vec4(l.xy, v, l.z) : r.w ? vec4(l.xyz, v) : l;
}
vec4 d(vec3 p){
    vec3 n = floor(p) + 0.5;
    vec4 d4 = vec4(length(1.5 - abs(p - n)));
    for(float k = 0.0; k <= 4.0; k++){
        vec3 g;
        g.z = n.z + sign(mod(k, 2.0) - 0.5) * ceil(k * 0.5);
        if (abs(g.z - p.z) - 0.5 > d4.w){
            continue;
        }
        for(float j = 0.0; j <= 4.0; j++){
            g.y = n.y + sign(mod(j, 2.0) - 0.5) * ceil(j * 0.5);
            if (abs(g.y - p.y) - 0.5 > d4.w){
                continue;
            }
            for(float i = -2.0; i <= 2.0; i++){
                g.x = n.x + i;
                vec3 j = hs(g) - 0.5;
                d4 = s(d4, length(g + j - p));
            }
        }
    }
    return d4;
}
vec4 w = vec4(-0.9, 0.0, -1.3, 2.5);
float n(vec3 p){ 
    return abs(dot(w * 1.0, d(p)));
}
float c(vec2 p, vec2 c, float r){
    float d = 10.0;
    return pow(dot(p - c, p - c), d) - r;
}
vec3 cntr(float v, float i){ 
    return abs(v) < 0.01 ? vec3(1.0, 1.0, 0.0) : mod(v, i) < 0.001 ? vec3(0.8) : vec3(0.0);
}
void mainImage(out vec4 fc, in vec2 fcCoord){
    vec2 p = (2.0 * fcCoord.xy - iResolution.xy) / min(iResolution.x, iResolution.y);
    float n = n(vec3(p.x, p.x, iTime * 0.8));
    float i = 0.01887 - n * 0.02;
    fc.rgb = vec3(cntr(c(vec2(3.8 * p * n), vec2(0.0), 0.04 * n), i));
    fc.a = 1.0;
}
